import React from "react";
import { useNavigate } from "react-router-dom";
import { useState } from "react";

const Footer = () => {
  const navigate = useNavigate();
  const [showPopup, setShowPopup] = useState(false);
  const handleLogout = () => {
    // Clear local storage
    localStorage.clear();

    // Clear cookies
    document.cookie.split(";").forEach((cookie) => {
      document.cookie = cookie
        .replace(/^ +/, "")
        .replace(/=.*/, "=;expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/");
    });
    navigate("/");

    alert("You have been logged out.");
  };

  return (
    <footer className="bg-gray-800 text-sm p-4 w-[100vw] h-[15vh] bottom-0 right-0 inline-flex">
      <button
        onClick={() => setShowPopup(true)}
        className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-all"
      >
        Logout
      </button>
      <p className="text-center text-white">&copy; Avilambh2025</p>
      <p className="text-right  text-white">
        <strong>Owner:</strong> Mr 979797XXXX
      </p>

      {showPopup && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center">
            <p className="mb-4 text-lg font-semibold">
              Logout from the System?
            </p>
            <div className="flex justify-center gap-4">
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-all"
              >
                Yes
              </button>
              <button
                onClick={() => setShowPopup(false)}
                className="bg-gray-300 text-black px-4 py-2 rounded-lg hover:bg-gray-400 transition-all"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}
    </footer>
  );
};

export default Footer;
