import React from "react";
import axios from "axios";
import Footer from "./Footer";
import { useState, useEffect } from "react";
import { Link } from "react-router-dom";

const Inventory = () => {
  const [inventory, setInventory] = useState([]);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isAddModalOpen, setIsAddModalOpen] = useState(false);
  const [selectedItem, setSelectedItem] = useState(null);
  const [newItem, setNewItem] = useState({
    Inventory_Id: "",
    item_name: "",
    quantity: "",
    reorder_level: "",
  });

  // Handle input change
  const handleChange = (e) => {
    setNewItem({ ...newItem, [e.target.name]: e.target.value });
  };

  useEffect(() => {
    axios
      .get("http://localhost:5000/inventory", {
        withCredentials: true, // âœ… Important: Send cookies
      }) // Update if deployed
      .then((response) => {
        setInventory(response.data);
      })
      .catch((error) => {
        console.error("Error fetching inventory data:", error);
      });
  }, []);

  //popup form functions
  const openModal = (item) => {
    setSelectedItem(item);
    setIsModalOpen(true);
  };

  const closeModal = () => {
    setIsModalOpen(false);
    setSelectedItem(null);
  };

  const openAddModal = () => {
    setIsAddModalOpen(true);
  };

  const closeAddModal = () => {
    setIsAddModalOpen(false);
    setNewItem({
      Inventory_Id: "INV90",
      item_name: "",
      quantity: "",
      reorder_level: "",
    });
  };

  const addInventory = async (e) => {
    e.preventDefault();
    console.log(newItem);
    try {
      const response = await axios.post(
        "http://localhost:5000/inventory/addItem",
        newItem
      );
      console.log("Data saved:", response.data);
      window.location.reload();
      alert("Form submitted successfully!");
    } catch (error) {
      alert("Error submitting form");
    }
  };

  return (
    <>
      <div className="flex h-[85vh]">
        {/* Sidebar */}
        <aside className="w-[20vw]  bg-[#4A4752] text-white p-6 flex flex-col justify-between">
          <div>
            <h1 className="text-4xl font-bold text-[#359BD2] text-center">
              AVILAMBH
            </h1>
            <nav className="mt-6 space-y-4 pt-24 pl-8">
              <Link
                to="/home"
                className="block text-gray-300 hover:text-white "
              >
                HOME
              </Link>
              <Link
                to="/reports"
                className="block text-gray-300 hover:text-white"
              >
                REPORTS
              </Link>
              <Link
                to="/safety"
                className="block text-gray-300 hover:text-white"
              >
                SAFETY
              </Link>
              <Link
                to="/transport"
                className="block text-gray-300 hover:text-white"
              >
                TRANSPORT
              </Link>
              <Link
                to="/inventory"
                className="block text-gray-300 hover:text-white underline"
              >
                INVENTORY
              </Link>
            </nav>
          </div>
        </aside>
      </div>
      <div className="w-[80vw] h-[85vh] absolute top-0 right-0 mt-4">
        <div className="max-h-[400px] overflow-y-auto">
          <table className="w-4/5 mx-auto border border-gray-300 shadow-md ">
            <thead class="bg-gray-700 text-white  uppercase text-left sticky top-0 z-5">
              <tr className="text-center">
                <th className="px-4 py-2">ID</th>
                <th className="px-4 py-2">Name</th>
                <th className="px-4 py-2">Quantity</th>
                <th className="px-4 py-2">Reorder Level</th>
                <th className="px-4 py-2">Action</th>
              </tr>
            </thead>
            <tbody className="max-h-[400px] overflow-y-auto scrollbar-hide">
              {inventory.map((item) => (
                <tr key={item.id} className="text-center">
                  <td className="border border-gray-300 px-4 py-2">
                    {item.Inventory_Id}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {item.item_name}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {item.quantity}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    {item.reorder_level}
                  </td>
                  <td className="border border-gray-300 px-4 py-2">
                    <button
                      className="px-4 py-1 bg-[#ff2188] rounded-md text-white outline-none"
                      onClick={() => openModal(item)}
                    >
                      update
                    </button>
                    <button
                      type="button"
                      className="px-4 py-1 ml-2 bg-red-500 rounded-md text-white outline-none"
                    >
                      Delete
                    </button>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>

          <button
            className="absolute bottom-6 right-32 bg-blue-500 text-white px-6 py-1 rounded"
            onClick={() => openAddModal()}
          >
            Add
          </button>
        </div>
      </div>

      {/* popup form to update or delete*/}
      {isModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-1/3">
            <h2 className="text-xl font-bold mb-4">Update Inventory</h2>
            <form>
              <label className="block mb-2">Item Id</label>
              <input
                type="text"
                defaultValue={selectedItem?.Inventory_Id}
                className="w-full p-2 border rounded mb-4"
                readOnly="readonly"
              />
              <label className="block mb-2">Item Name</label>
              <input
                type="text"
                defaultValue={selectedItem?.item_name}
                className="w-full p-2 border rounded mb-4"
              />

              <label className="block mb-2">Quantity</label>
              <input
                type="number"
                defaultValue={selectedItem?.quantity}
                className="w-full p-2 border rounded mb-4"
              />

              <label className="block mb-2">Reorder Level</label>
              <input
                type="number"
                defaultValue={selectedItem?.reorder_level}
                className="w-full p-2 border rounded mb-4"
              />

              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  className="px-4 py-2 bg-gray-400 rounded-md text-white"
                  onClick={closeModal}
                >
                  Cancel
                </button>
                <button
                  type="submit"
                  className="px-4 py-2 bg-blue-600 rounded-md text-white"
                >
                  Save
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      {/* popup form to add */}
      {isAddModalOpen && (
        <div className="fixed inset-0 flex items-center justify-center bg-black bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg w-1/3">
            <h2 className="text-xl font-bold mb-4">Add New Item</h2>
            <form>
              <label className="block mb-2">Inventory</label>
              <input
                name="Inventory_Id"
                type="text"
                className="w-full p-2 border rounded mb-4"
                value={newItem.Inventory_Id}
                onChange={handleChange}
              />
              <label className="block mb-2">Item Name</label>
              <input
                name="item_name"
                type="text"
                className="w-full p-2 border rounded mb-4"
                value={newItem.item_name}
                onChange={handleChange}
              />
              <label className="block mb-2">Quantity</label>
              <input
                name="quantity"
                type="number"
                className="w-full p-2 border rounded mb-4"
                value={newItem.quantity}
                onChange={handleChange}
              />
              <label className="block mb-2">Reorder Level</label>
              <input
                name="reorder_level"
                type="number"
                className="w-full p-2 border rounded mb-4"
                value={newItem.reorder_level}
                onChange={handleChange}
              />
              <div className="flex justify-end space-x-2">
                <button
                  type="button"
                  className="px-4 py-2 bg-gray-400 rounded-md text-white"
                  onClick={closeAddModal}
                >
                  Cancel
                </button>
                <button
                  type="button"
                  className="px-4 py-2 bg-green-600 rounded-md text-white"
                  onClick={addInventory}
                >
                  Add
                </button>
              </div>
            </form>
          </div>
        </div>
      )}

      <Footer />
    </>
  );
};

export default Inventory;
