import React from "react";
import { useNavigate } from "react-router-dom";
import {useEffect,useState } from "react";
import axios from "axios";
import {jwtDecode} from "jwt-decode";

const Footer = () => {
  const navigate = useNavigate();
  const [showPopup, setShowPopup] = useState(false);
  const [userData, setUserData] = useState(null);
  useEffect(() => {
    axios
      .post("http://localhost:5000/auth/login", { withCredentials: true })
      .then((response) => {
        if (response.data.token) {
          const decoded = jwtDecode(response.data.token);
          setUserData(decoded);
        }
        console.log(userData)
      })
      .catch((error) => console.error("Error fetching token:", error));
  }, []);



  const handleLogout = () => {
    // Clear local storage
    localStorage.clear();

    // Clear cookies
    document.cookie.split(";").forEach((cookie) => {
      document.cookie = "jwtToken=; expires=Thu, 01 Jan 1970 00:00:00 UTC; path=/";
    });
    navigate("/");
  };

  return (
    <footer className="bg-gray-900 py-4 px-6 h-[10vh] flex justify-between items-center">
      <button
        onClick={() => setShowPopup(true)}
        className="bg-red-500 hover:bg-red-600 text-white font-semibold py-2 px-4 rounded-lg transition duration-300"
      >
        Logout
      </button>
      <p className="text-sm text-gray-400">&copy; Avilambh2025</p>
      <p className="text-sm text-gray-300">
        <span className="font-bold">Owner:</span> Mr 979797XXXX
      </p>

      {showPopup && (
        <div className="fixed inset-0 flex items-center justify-center bg-gray-900 bg-opacity-50">
          <div className="bg-white p-6 rounded-lg shadow-lg text-center">
            <p className="mb-4 text-lg font-semibold">
              Logout from the System?
            </p>
            <div className="flex justify-center gap-4">
              <button
                onClick={handleLogout}
                className="bg-red-500 text-white px-4 py-2 rounded-lg hover:bg-red-600 transition-all"
              >
                Yes
              </button>
              <button
                onClick={() => setShowPopup(false)}
                className="bg-gray-300 text-black px-4 py-2 rounded-lg hover:bg-gray-400 transition-all"
              >
                No
              </button>
            </div>
          </div>
        </div>
      )}
    </footer>
  );
};

export default Footer;
